create view SITATSA.V_CAIS_TIT as 
	select cais.caisim, cais.descat, 
	cais.tiplavg, tab5nord titnord, cais.titcat, 
	cais.codliv1, cais.codliv2, cais.codliv3, cais.codliv4,
	cais1.descat desliv1, cais2.descat desliv2, cais3.descat desliv3, cais4.descat desliv4,
	cais.caisord, cais.quaobb, cais.acontec, cais.isarchi,
	case when (select count(*) from SITATSA.cais cais5 where cais5.codliv1=cais.caisim or cais5.codliv2=cais.caisim or cais5.codliv3=cais.caisim or cais5.codliv4=cais.caisim)=0 then '1' else '2' end isfoglia,
	case when cais.codliv4 is not null then 5 when cais.codliv3 is not null then 4 when cais.codliv2 is not null then 3 when cais.codliv1 is not null then 2 else 1 end numliv,
	(case when cais.tiplavg=4 then '0' else STRIP(digits(cais.tiplavg),L,'0') end) || 
	(case when tab5nord is null then '00000' else substr(digits(tab5nord),4) end) || 
	(case when tab5tip is null then '000' else lpad(tab5tip,3,'0') end) || 
	(case when cais.caisord is null then '000' else substr(digits(cais.caisord),5) end) ||
	(case when cais.codliv4 is not null then 
	 rpad(cais.codliv1,30,'*') || rpad(cais.codliv2,30,'*') ||
	 rpad(cais.codliv3,30,'*') || rpad(cais.codliv4,30,'*') || rpad(cais.caisim,30,'*') 
	 when cais.codliv3 is not null then 
	 rpad(cais.codliv1,30,'*') || rpad(cais.codliv2,30,'*') || 
	 rpad(cais.codliv3,30,'*') || rpad(cais.caisim,30,'*') || '******************************'
	 when cais.codliv2 is not null then
	 rpad(cais.codliv1,30,'*') || rpad(cais.codliv2,30,'*') || 
	 rpad(cais.caisim,30,'*') || '******************************' || '******************************'
	 when cais.codliv1 is not null then
	 rpad(cais.codliv1,30,'*') || rpad(cais.caisim,30,'*') || 
	 '******************************' || '******************************' || '******************************'
	 else rpad(cais.caisim,30,'*') || '******************************' || 
	 '******************************' || '******************************' || '******************************' end) numord
	from SITATSA.cais cais left outer join SITATSA.tab5 on (CAIS.TITCAT = TAB5TIP and TAB5COD = 'G_j05')
	left outer join SITATSA.cais cais1 on (cais1.caisim=cais.codliv1)
	left outer join SITATSA.cais cais2 on (cais2.caisim=cais.codliv2)
	left outer join SITATSA.cais cais3 on (cais3.caisim=cais.codliv3)
	left outer join SITATSA.cais cais4 on (cais4.caisim=cais.codliv4);

